mapper.insert:
INSERT INTO tbl_cart(
   user_id, product_id, cart_quantity
) VALUES (
   #{user_id}, #{product_id}, #{cart_quantity}         
)

mapper.update:
UPDATE tbl_cart
SET cart_quantity = #{cart_quantity}
WHERE user_id= #{user_id}
AND product_id = #{product_id}

mapper.select:
SELECT COUNT(*)
FORM tbl_cart
WHERE user_id = #{user_id}
AND product_id =#{product_id}

mapper.update:
UPDATE tbl_cart
SET cart_quantity = cart_quantity + #{cart_quantity}
WHERE user_id= #{user_id}
AND product_id = #{product_id}
