mapper.insert:
INSERT INTO cart(
   user_id, product_id, amount
) VALUES (
   #{user_id}, #{product_id}, #{amount}         
)

mapper.update:
UPDATE cart
SET amount = #{amount}
WHERE user_id= #{user_id}
AND product_id = #{product_id}

mapper.select:
SELECT COUNT(*)
FROM cart
WHERE user_id = #{user_id}
AND product_id =#{product_id}

mapper.update:
UPDATE cart
SET amount = amount + #{amount}
WHERE user_id= #{user_id}
AND product_id = #{product_id}
