mapper.insert:
INSERT INTO tbl_cart(
	user_id, product_id, amount
) VALUES (
	#{user_id}, #{product_id}, #{amount}			
)

mapper.update:
UPDATE tbl_cart
SET amount = #{amount}
WHERE user_id= #{user_id}
AND product_id = #{product_id}

mapper.select:
SELECT COUNT(*)
FORM tbl_cart
WHERE user_id = #{user_id}
AND product_id =#{product_id}

mapper.update:
UPDATE tbl_cart
SET amount = amount + #{amount}
WHERE user_id= #{user_id}
AND product_id = #{product_id}
