mapper.insert:
INSERT INTO product(product_name, product_price, product_desc, product_url)
VALUES (
    #{product_name}
   ,#{product_price}
   ,#{product_desc}
   ,#{product_url})

mapper.update:
UPDATE product
SET product_name = #{product_name}
   ,product_price = #{product_price}
   ,product_desc = #{product_desc}
   ,product_url = #{product_url}
WHERE
   product_id = #{product_id}

mapper.select:
SELECT product_url
FROM product
WHERE product_id = #{product_id}
