		INSERT INTO reply (bno, replytext, replyer) VALUES (#{bno}, #{replytext}, #{replyer})
     
     
     
     
        SELECT rno, bno, replytext, replyer, r.regdate, r.updatedate, m.user_name, 
        (SELECT writer FROM board WHERE bno = r.bno) AS writer 
        FROM reply r LEFT JOIN user m on r.replyer = m.user_id 
        WHERE r.replyer = m.user_id AND bno=#{bno} 
        ORDER BY rno
          
     
     
     
        SELECT rno, bno, replytext, replyer, regdate, updatedate, secretReply FROM reply 
        WHERE rno = #{rno}
     
     
     
        UPDATE reply 
        SET replytext = #{rno} 
        WHERE rno = #{rno}
     
          
     
        DELETE FROM reply
        WHERE rno = #{rno}
     

   
        SELECT COUNT(*) FROM reply 
        WHERE bno = #{bno}
     

   
        LIMIT 10 OFFSET #{start}
     
     
